|desc=Makes "field" required and digits only.
|code=<nowiki>jQuery.validator.addMethod("phoneUS", function(phone_number, element) {
    phone_number = phone_number.replace(/\s+/g, "");
	return this.optional(element) || phone_number.length > 9 &&
		phone_number.match(/^(1-?)?(\([2-9]\d{2}\)|[2-9]\d{2})-?[2-9]\d{2}-?\d{4}$/);
}, "Please specify a valid phone number");</nowiki>

 {{Code|Core|$}}("#myform").{{Code|Plugins/Validation|validate}}<nowiki>({
  rules: {
    field: {
      required: true,
      phoneUS: true
    }
  }
});</nowiki>
}}

