	debug: true,
	success: "valid"
});;
</script>
</nowiki>
|desc=Makes details required only if #other is checked.
|html=<nowiki><form id="myform">
  <label>Age </label><input id="age" name="age" />
  <br/>
  <label>Parent </label><input id="parent" name="parent" />
  <br/>
  <input type="submit" value="Validate!" />
</form></nowiki>
|code={{Code|Core|$}}("#myform").{{Code|Plugins/Validation|validate}}<nowiki>({
  rules: {
    age: {
      required: true,
      min: 3
